/*
  Author: Hayden Stegman
*/
import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Button, Text, View } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { RootStackParamList } from './RootStackParamList';
import { StackNavigationProp } from '@react-navigation/native';

type HomeScreenProp = StackNavigationProp<RootStackParamList, 'HomePage'>;

/* 
  Home Screen
*/
const HomeScreen = ( ) => {
  return (
    <View style={styles.container}>
      
      {/* Page Heading */}
      <Text>Showing all Landlords from '05401'</Text>
      <StatusBar style="auto" />

      {/* List of Landlords */}
      <LandlordWidget name='Chris'/>
      <LandlordWidget name='Mark'/>
      <LandlordWidget name='Charlie'/>
      <LandlordWidget name='Sarah'/>
    </View>
  );
};

{/* 
  Widget added to list for each landlord present in area.
  Features:
    - Name
    - 1-5 Star Rating
    - Button to go to landlord page
*/}
const LandlordWidget = ( props:any, { navigation } ) => {
  return (
    <View style={{ backgroundColor: 'grey', padding: 10, margin: 2, width: '80%'}}>
      <Text>{props.name}</Text>
      
      <Button
        title={props.name + "'s Reviews"}
        onPress={() => navigation.navigate('LandlordPage')}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
